import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { UserService } from '@services/UserService';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent implements OnInit {
  user: any = {};
  editedUser: any = {};
  editMode: boolean = false;
  loading: boolean = true;
  isSaving: boolean = false;
  errorMessage: string = '';
  successMessage: string = '';

  constructor(
    private userService: UserService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadProfile();
  }

  loadProfile(): void {
    this.userService.getProfile().subscribe(
      (response: any) => {
        this.user = response.user || response;
        this.editedUser = { ...this.user };
        this.loading = false;
      },
      (error: any) => {
        console.log('Error carregant perfil', error);
        this.errorMessage = error.error.message || 'Error carregant el perfil';
        this.loading = false;
      }
    );
  }

  toggleEditMode(): void {
    this.editMode = !this.editMode;
    if (!this.editMode) {
      this.editedUser = { ...this.user };
    }
  }

  saveProfile(): void {
    if (!this.editedUser.name || !this.editedUser.email) {
      this.errorMessage = 'El nom i email són obligatoris';
      return;
    }

    this.isSaving = true;
    this.errorMessage = '';
    this.successMessage = '';

    this.userService.updateProfile(this.editedUser).subscribe(
      (response: any) => {
        this.isSaving = false;
        this.user = this.editedUser;
        this.editMode = false;
        this.successMessage = 'Perfil actualitzat correctament!';
        setTimeout(() => {
          this.successMessage = '';
        }, 3000);
      },
      (error: any) => {
        this.isSaving = false;
        this.errorMessage = error.error.message || 'Error actualitzant el perfil';
      }
    );
  }

  changePassword(): void {
    const newPassword = prompt('Introdueix la nova contrasenya:');
    if (!newPassword) return;

    if (newPassword.length < 6) {
      this.errorMessage = 'La contrasenya ha de tenir almenys 6 caràcters';
      return;
    }

    this.errorMessage = '';
    this.successMessage = '';

    // Aquí hauries de cridar a un servei changePassword
    // De moment, mostrem un missatge
    this.successMessage = 'Contrasenya canviada correctament!';
    setTimeout(() => {
      this.successMessage = '';
    }, 3000);
  }

  deleteAccount(): void {
    const confirm = window.confirm(
      'Estàs segur que vols eliminar el teu compte? Aquesta acció no es pot desfer.'
    );
    if (!confirm) return;

    this.errorMessage = '';
    this.successMessage = '';

    // Aquí hauries de cridar a un servei deleteAccount
    this.successMessage = 'Compte eliminat correctament';
    setTimeout(() => {
      localStorage.removeItem('token');
      this.router.navigate(['/login']);
    }, 2000);
  }

  goHome(): void {
    this.router.navigate(['/home']);
  }
}
<div class="profile-container">
  <div class="header">
    <h1>El Meu Perfil</h1>
    <button class="btn-back" (click)="goHome()">Tornar al Home</button>
  </div>

  <div *ngIf="loading" class="loading">
    Carregant perfil...
  </div>

  <div *ngIf="!loading" class="profile-content">
    <div class="profile-card">
      <div class="avatar">
        <div class="avatar-placeholder">{{ user.name | slice:0:1 }}</div>
      </div>

      <div class="user-info">
        <div class="info-group">
          <label>Nom Complet:</label>
          <p *ngIf="!editMode" class="info-value">{{ user.name }}</p>
          <input
            *ngIf="editMode"
            type="text"
            [(ngModel)]="editedUser.name"
            class="edit-input"
          />
        </div>

        <div class="info-group">
          <label>Email:</label>
          <p *ngIf="!editMode" class="info-value">{{ user.email }}</p>
          <input
            *ngIf="editMode"
            type="email"
            [(ngModel)]="editedUser.email"
            class="edit-input"
          />
        </div>

        <div class="info-group">
          <label>Telefon:</label>
          <p *ngIf="!editMode" class="info-value">{{ user.phone || 'No indicat' }}</p>
          <input
            *ngIf="editMode"
            type="text"
            [(ngModel)]="editedUser.phone"
            class="edit-input"
          />
        </div>

        <div class="info-group">
          <label>Adreça:</label>
          <p *ngIf="!editMode" class="info-value">{{ user.address || 'No indicada' }}</p>
          <input
            *ngIf="editMode"
            type="text"
            [(ngModel)]="editedUser.address"
            class="edit-input"
          />
        </div>

        <div class="info-group">
          <label>Data de Registre:</label>
          <p class="info-value">{{ user.createdAt | date: 'dd/MM/yyyy' }}</p>
        </div>

        <div class="info-group">
          <label>Valoració:</label>
          <p class="info-value">{{ user.rating || '5.0' }} / 10</p>
        </div>
      </div>
    </div>

    <div class="actions">
      <button
        *ngIf="!editMode"
        class="btn btn-primary"
        (click)="toggleEditMode()"
      >
        Editar Perfil
      </button>

      <div *ngIf="editMode" class="edit-actions">
        <button
          class="btn btn-success"
          (click)="saveProfile()"
          [disabled]="isSaving"
        >
          {{ isSaving ? 'Guardant...' : 'Guardar Canvis' }}
        </button>
        <button
          class="btn btn-secondary"
          (click)="toggleEditMode()"
          [disabled]="isSaving"
        >
          Cancelar
        </button>
      </div>

      <button class="btn btn-warning" (click)="changePassword()">
        Canviar Contrasenya
      </button>

      <button class="btn btn-danger" (click)="deleteAccount()">
        Eliminar Compte
      </button>
    </div>

    <div *ngIf="successMessage" class="success">
      {{ successMessage }}
    </div>

    <div *ngIf="errorMessage" class="error">
      {{ errorMessage }}
    </div>
  </div>
</div>
