import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { BookService } from '@services/BookService';
import { LoanService } from '@services/LoanService';

@Component({
  selector: 'app-books',
  templateUrl: './books.component.html',
  styleUrls: ['./books.component.css']
})
export class BooksComponent implements OnInit {
  books: any[] = [];
  filteredBooks: any[] = [];
  searchQuery: string = '';
  loading: boolean = true;
  isLoaning: boolean = false;
  errorMessage: string = '';
  successMessage: string = '';

  constructor(
    private bookService: BookService,
    private loanService: LoanService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadBooks();
  }

  loadBooks(): void {
    this.bookService.getAllBooks().subscribe(
      (response: any) => {
        this.books = response.books || response;
        this.filteredBooks = this.books;
        this.loading = false;
      },
      (error: any) => {
        console.log('Error carregant llibres', error);
        this.errorMessage = error.error.message || 'Error carregant els llibres';
        this.loading = false;
      }
    );
  }

  filterBooks(): void {
    if (!this.searchQuery.trim()) {
      this.filteredBooks = this.books;
      return;
    }

    const query = this.searchQuery.toLowerCase();
    this.filteredBooks = this.books.filter(book =>
      book.titol.toLowerCase().includes(query) ||
      book.autor.toLowerCase().includes(query)
    );
  }

  borrowBook(bookId: string): void {
    this.isLoaning = true;
    this.errorMessage = '';
    this.successMessage = '';

    this.loanService.createLoan(bookId).subscribe(
      (response: any) => {
        this.isLoaning = false;
        this.successMessage = 'PrÃ©stec solicitat correctament!';
        setTimeout(() => {
          this.loadBooks();
          this.successMessage = '';
        }, 2000);
      },
      (error: any) => {
        this.isLoaning = false;
        this.errorMessage = error.error.message || 'Error solicitant el prÃ©stec';
      }
    );
  }

  goHome(): void {
    this.router.navigate(['/home']);
  }
}
<div class="books-container">
  <div class="header">
    <h1>CatÃ leg de Llibres</h1>
    <button class="btn-back" (click)="goHome()">Tornar al Home</button>
  </div>

  <div class="search-section">
    <input
      type="text"
      [(ngModel)]="searchQuery"
      placeholder="Buscar per tÃ­tol o autor..."
      class="search-input"
    />
    <button class="btn-search" (click)="filterBooks()">Buscar</button>
  </div>

  <div *ngIf="loading" class="loading">
    Carregant llibres...
  </div>

  <div *ngIf="!loading && filteredBooks.length === 0" class="empty-state">
    <p>No s'han trobat llibres</p>
  </div>

  <div *ngIf="!loading && filteredBooks.length > 0" class="books-section">
    <div class="books-grid">
      <div *ngFor="let book of filteredBooks" class="book-card">
        <div class="book-cover">
          <div class="cover-placeholder">
            ðŸ“š
          </div>
        </div>

        <div class="book-info">
          <h3 class="book-title">{{ book.titol }}</h3>
          <p class="book-author">{{ book.autor }}</p>
          <p class="book-category">{{ book.categoria }}</p>

          <div class="book-details">
            <div class="detail">
              <span class="label">ISBN:</span>
              <span class="value">{{ book.isbn }}</span>
            </div>
            <div class="detail">
              <span class="label">Any:</span>
              <span class="value">{{ book.any }}</span>
            </div>
            <div class="detail">
              <span class="label">CÃ²pies Disponibles:</span>
              <span class="value" [ngClass]="book.quantitatDisponible === 0 ? 'no-available' : ''">
                {{ book.quantitatDisponible }}
              </span>
            </div>
          </div>

          <button
            class="btn-loan"
            (click)="borrowBook(book._id)"
            [disabled]="book.quantitatDisponible === 0 || isLoaning"
          >
            {{ isLoaning ? 'Solicitant...' : book.quantitatDisponible === 0 ? 'No Disponible' : 'Solicitar PrÃ©stec' }}
          </button>
        </div>
      </div>
    </div>
  </div>

  <div *ngIf="successMessage" class="success">
    {{ successMessage }}
  </div>

  <div *ngIf="errorMessage" class="error">
    {{ errorMessage }}
  </div>
</div>
