import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { UserService } from '@services/UserService';
import { BookService } from '@services/BookService';
import { LoanService } from '@services/LoanService';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent implements OnInit {
  user: any = {};
  editedUser: any = {};
  editMode: boolean = false;
  loading: boolean = true;
  isSaving: boolean = false;
  errorMessage: string = '';
  successMessage: string = '';
  
  // Stats per admin
  totalUsers: number = 0;
  totalBooks: number = 0;
  totalLoans: number = 0;

  constructor(
    private userService: UserService,
    private bookService: BookService,
    private loanService: LoanService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadProfile();
  }

  loadProfile(): void {
    this.userService.getProfile().subscribe(
      (response: any) => {
        this.user = response.user || response;
        this.editedUser = { ...this.user };
        this.loading = false;
        
        // Si és admin, carregar estadístiques
        if (this.isAdmin()) {
          this.loadAdminStats();
        }
      },
      (error: any) => {
        console.log('Error carregant perfil', error);
        this.errorMessage = error.error?.message || 'Error carregant el perfil';
        this.loading = false;
      }
    );
  }

  loadAdminStats(): void {
    // Carregar total usuaris
    this.userService.getAllUsers().subscribe(
      (users: any) => {
        this.totalUsers = users?.length || 0;
      },
      (error: any) => console.log('Error carregant usuaris', error)
    );

    // Carregar total llibres
    this.bookService.getAllBooks().subscribe(
      (books: any) => {
        this.totalBooks = books?.length || 0;
      },
      (error: any) => console.log('Error carregant llibres', error)
    );

    // Carregar préstecs actius
    this.loanService.getAllLoans().subscribe(
      (loans: any) => {
        this.totalLoans = (loans || []).filter((l: any) => l.estat === 'ACTIU').length;
      },
      (error: any) => console.log('Error carregant préstecs', error)
    );
  }

  isAdmin(): boolean {
    return this.user?.rol === 'ADMIN';
  }

  toggleEditMode(): void {
    this.editMode = !this.editMode;
    if (!this.editMode) {
      this.editedUser = { ...this.user };
    }
  }

  saveProfile(): void {
    if (!this.canSave()) {
      this.errorMessage = 'El nom i email són obligatoris';
      return;
    }

    this.isSaving = true;
    this.errorMessage = '';
    this.successMessage = '';

    this.userService.updateProfile(this.editedUser).subscribe(
      (response: any) => {
        this.isSaving = false;
        this.user = { ...this.editedUser };
        this.editMode = false;
        this.successMessage = 'Perfil actualitzat correctament!';
        setTimeout(() => {
          this.successMessage = '';
        }, 3000);
      },
      (error: any) => {
        this.isSaving = false;
        this.errorMessage = error.error.message || 'Error actualitzant el perfil';
      }
    );
  }

  canSave(): boolean {
    return !!(this.editedUser.nom?.trim() && this.editedUser.email?.trim());
  }

  changePassword(): void {
    const newPassword = prompt('Introdueix la nova contrasenya:');
    if (!newPassword) return;

    if (newPassword.length < 6) {
      this.errorMessage = 'La contrasenya ha de tenir almenys 6 caràcters';
      return;
    }

    this.errorMessage = '';
    this.successMessage = '';

    // Aquí hauries de cridar a un servei changePassword
    // De moment, mostrem un missatge
    this.successMessage = 'Contrasenya canviada correctament!';
    setTimeout(() => {
      this.successMessage = '';
    }, 3000);
  }

  deleteAccount(): void {
    const confirm = window.confirm(
      'Estàs segur que vols eliminar el teu compte? Aquesta acció no es pot desfer.'
    );
    if (!confirm) return;

    this.errorMessage = '';
    this.successMessage = '';

    // Aquí hauries de cridar a un servei deleteAccount
    this.successMessage = 'Compte eliminat correctament';
    setTimeout(() => {
      localStorage.removeItem('token');
      this.router.navigate(['/login']);
    }, 2000);
  }

  goHome(): void {
    this.router.navigate(['/home']);
  }

  // Funcions d'Admin
  goToManageBooks(): void {
    this.router.navigate(['/admin/books']);
  }

  goToManageUsers(): void {
    this.router.navigate(['/admin/users']);
  }

  goToManageLoans(): void {
    this.router.navigate(['/admin/loans']);
  }
}
<div class="profile-container">
  <!-- Header amb gradient -->
  <div class="profile-header">
    <div class="header-content">
      <button class="btn-back" (click)="goHome()">
        <span class="back-icon">←</span> Tornar
      </button>
      <h1>El Meu Perfil</h1>
      <div class="role-badge" [class.admin]="isAdmin()">
        {{ isAdmin() ? '👑 Administrador' : '👤 Usuari' }}
      </div>
    </div>
  </div>

  <!-- Loading -->
  <div *ngIf="loading" class="loading-container">
    <div class="spinner"></div>
    <p>Carregant perfil...</p>
  </div>

  <div *ngIf="!loading" class="profile-content">
    <!-- Missatges -->
    <div *ngIf="successMessage" class="toast success">
      <span class="toast-icon">✓</span>
      {{ successMessage }}
    </div>
    <div *ngIf="errorMessage" class="toast error">
      <span class="toast-icon">✕</span>
      {{ errorMessage }}
    </div>

    <!-- Targeta Principal del Perfil -->
    <div class="main-card">
      <div class="avatar-section">
        <div class="avatar-wrapper">
          <div class="avatar-circle">{{ getInitial() }}</div>
          <div class="avatar-status" [class.online]="true"></div>
        </div>
        <h2 class="user-name">{{ user.nom }}</h2>
        <p class="user-email">{{ user.email }}</p>
      </div>

      <!-- Informació Personal -->
      <div class="info-section">
        <h3 class="section-title">
          <span class="section-icon">📋</span>
          Informació Personal
        </h3>
        
        <div class="info-grid">
          <div class="info-card">
            <div class="info-label">
              <span class="label-icon">👤</span>
              Nom Complet
            </div>
            <div *ngIf="!editMode" class="info-value">{{ user.nom }}</div>
            <input
              *ngIf="editMode"
              type="text"
              [(ngModel)]="editedUser.nom"
              class="edit-input"
              [class.invalid]="!editedUser.nom?.trim()"
              placeholder="El nom és obligatori"
            />
            <span *ngIf="editMode && !editedUser.nom?.trim()" class="field-error">Camp obligatori</span>
          </div>

          <div class="info-card">
            <div class="info-label">
              <span class="label-icon">📧</span>
              Correu Electrònic
            </div>
            <div *ngIf="!editMode" class="info-value">{{ user.email }}</div>
            <input
              *ngIf="editMode"
              type="email"
              [(ngModel)]="editedUser.email"
              class="edit-input"
              [class.invalid]="!editedUser.email?.trim()"
              placeholder="L'email és obligatori"
            />
            <span *ngIf="editMode && !editedUser.email?.trim()" class="field-error">Camp obligatori</span>
          </div>

          <div class="info-card">
            <div class="info-label">
              <span class="label-icon">📱</span>
              Telèfon
            </div>
            <div *ngIf="!editMode" class="info-value">{{ user.telefon || 'No indicat' }}</div>
            <input
              *ngIf="editMode"
              type="text"
              [(ngModel)]="editedUser.telefon"
              class="edit-input"
              placeholder="Telèfon (opcional)"
            />
          </div>

          <div class="info-card">
            <div class="info-label">
              <span class="label-icon">🏠</span>
              Adreça
            </div>
            <div *ngIf="!editMode" class="info-value">{{ user.adreca || 'No indicada' }}</div>
            <input
              *ngIf="editMode"
              type="text"
              [(ngModel)]="editedUser.adreca"
              class="edit-input"
              placeholder="Adreça (opcional)"
            />
          </div>

          <div class="info-card">
            <div class="info-label">
              <span class="label-icon">📅</span>
              Membre des de
            </div>
            <div class="info-value">{{ user.createdAt | date: 'dd MMMM yyyy' }}</div>
          </div>

          <div class="info-card">
            <div class="info-label">
              <span class="label-icon">🔐</span>
              Tipus de Compte
            </div>
            <div class="info-value role-value" [class.admin]="isAdmin()">
              {{ isAdmin() ? 'Administrador' : 'Usuari Estàndard' }}
            </div>
          </div>
        </div>
      </div>

      <!-- Accions del Perfil -->
      <div class="actions-section">
        <h3 class="section-title">
          <span class="section-icon">⚙️</span>
          Accions del Compte
        </h3>
        
        <div class="actions-grid">
          <button
            *ngIf="!editMode"
            class="action-btn primary"
            (click)="toggleEditMode()"
          >
            <span class="btn-icon">✏️</span>
            Editar Perfil
          </button>

          <div *ngIf="editMode" class="edit-actions">
            <button
              class="action-btn success"
              (click)="saveProfile()"
              [disabled]="isSaving || !canSave()"
            >
              <span class="btn-icon">💾</span>
              {{ isSaving ? 'Guardant...' : 'Guardar' }}
            </button>
            <button
              class="action-btn secondary"
              (click)="toggleEditMode()"
              [disabled]="isSaving"
            >
              <span class="btn-icon">↩️</span>
              Cancel·lar
            </button>
          </div>

          <button *ngIf="!editMode" class="action-btn warning" (click)="changePassword()">
            <span class="btn-icon">🔑</span>
            Canviar Contrasenya
          </button>

          <button *ngIf="!editMode && !isAdmin()" class="action-btn danger" (click)="deleteAccount()">
            <span class="btn-icon">🗑️</span>
            Eliminar Compte
          </button>
        </div>
      </div>
    </div>

    <!-- Secció Admin -->
    <div *ngIf="isAdmin()" class="admin-section">
      <div class="admin-card">
        <div class="admin-header">
          <h3>
            <span class="admin-icon">👑</span>
            Panel d'Administrador
          </h3>
          <p>Accés exclusiu per gestionar la biblioteca</p>
        </div>
        
        <div class="admin-stats">
          <div class="stat-item">
            <span class="stat-number">{{ totalUsers }}</span>
            <span class="stat-label">Usuaris</span>
          </div>
          <div class="stat-item">
            <span class="stat-number">{{ totalBooks }}</span>
            <span class="stat-label">Llibres</span>
          </div>
          <div class="stat-item">
            <span class="stat-number">{{ totalLoans }}</span>
            <span class="stat-label">Préstecs Actius</span>
          </div>
        </div>

        <div class="admin-actions">
          <button class="admin-btn" (click)="goToManageBooks()">
            <span class="admin-btn-icon">📚</span>
            <span class="admin-btn-text">Gestionar Llibres</span>
            <span class="admin-btn-desc">Afegir, editar o eliminar llibres</span>
          </button>
          
          <button class="admin-btn" (click)="goToManageUsers()">
            <span class="admin-btn-icon">👥</span>
            <span class="admin-btn-text">Gestionar Usuaris</span>
            <span class="admin-btn-desc">Veure i administrar usuaris</span>
          </button>
          
          <button class="admin-btn" (click)="goToManageLoans()">
            <span class="admin-btn-icon">📋</span>
            <span class="admin-btn-text">Gestionar Préstecs</span>
            <span class="admin-btn-desc">Veure tots els préstecs</span>
          </button>
        </div>
      </div>
    </div>
  </div>
</div>
