import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '@services/AuthService';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Biblioteca - Gestió de Préstecs';

  constructor(
    private authService: AuthService,
    private router: Router
  ) {}

  isLoggedIn(): boolean {
    return this.authService.isAuthenticated();
  }

  logout(): void {
    this.authService.logout();
    this.router.navigate(['/login']);
  }
}
<header>
  <h1>{{ title }}</h1>
  <nav>
    <ul>
      <!-- Mostrar quan NO està autenticat -->
      <ng-container *ngIf="!isLoggedIn()">
        <li><a routerLink="/login" routerLinkActive="active">Login</a></li>
        <li><a routerLink="/register" routerLinkActive="active">Registre</a></li>
      </ng-container>
      
      <!-- Mostrar quan SÍ està autenticat -->
      <ng-container *ngIf="isLoggedIn()">
        <li><a routerLink="/home" routerLinkActive="active">Home</a></li>
        <li><a routerLink="/books" routerLinkActive="active">Llibres</a></li>
        <li><a routerLink="/my-loans" routerLinkActive="active">Els Meus Préstecs</a></li>
        <li><a routerLink="/profile" routerLinkActive="active">Perfil</a></li>
        <li><a (click)="logout()" class="logout-link">Logout</a></li>
      </ng-container>
    </ul>
  </nav>
</header>

<main>
  <router-outlet></router-outlet>
</main>

<footer>
  <p>&copy; 2026 Biblioteca. Tots els drets reservats.</p>
</footer>
