import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { UserService } from '@services/UserService';

@Component({
  selector: 'app-admin-users',
  templateUrl: './admin-users.component.html',
  styleUrl: './admin-users.component.css'
})
export class AdminUsersComponent implements OnInit {
  users: any[] = [];
  loading: boolean = true;
  searchTerm: string = '';
  successMessage: string = '';
  errorMessage: string = '';

  constructor(
    private userService: UserService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadUsers();
  }

  loadUsers(): void {
    this.userService.getAllUsers().subscribe(
      (users: any) => {
        this.users = users || [];
        this.loading = false;
      },
      (error: any) => {
        this.errorMessage = 'Error carregant els usuaris';
        this.loading = false;
      }
    );
  }

  get filteredUsers(): any[] {
    if (!this.searchTerm) return this.users;
    const term = this.searchTerm.toLowerCase();
    return this.users.filter(user =>
      user.nom?.toLowerCase().includes(term) ||
      user.email?.toLowerCase().includes(term)
    );
  }

  getRoleBadgeClass(rol: string): string {
    return rol === 'ADMIN' ? 'admin' : 'user';
  }

  goBack(): void {
    this.router.navigate(['/profile']);
  }
}
<div class="admin-container">
  <div class="admin-header">
    <div class="header-content">
      <button class="btn-back" (click)="goBack()">‚Üê Tornar</button>
      <h1>üë• Gesti√≥ d'Usuaris</h1>
      <div class="header-spacer"></div>
    </div>
  </div>

  <!-- Messages -->
  <div *ngIf="successMessage" class="toast success">‚úì {{ successMessage }}</div>
  <div *ngIf="errorMessage" class="toast error">‚úï {{ errorMessage }}</div>

  <div class="content">
    <!-- Search -->
    <div class="search-bar">
      <input type="text" [(ngModel)]="searchTerm" placeholder="üîç Cercar per nom o email...">
      <span class="count">{{ filteredUsers.length }} usuaris</span>
    </div>

    <!-- Loading -->
    <div *ngIf="loading" class="loading">
      <div class="spinner"></div>
      <p>Carregant usuaris...</p>
    </div>

    <!-- Users Grid -->
    <div *ngIf="!loading" class="users-grid">
      <div *ngFor="let user of filteredUsers" class="user-card">
        <div class="user-avatar">{{ user.nom?.charAt(0)?.toUpperCase() || '?' }}</div>
        <div class="user-info">
          <h3>{{ user.nom }}</h3>
          <p class="email">{{ user.email }}</p>
          <p class="details">
            <span *ngIf="user.telefon">üì± {{ user.telefon }}</span>
            <span *ngIf="!user.telefon">üì± No indicat</span>
          </p>
          <p class="date">üìÖ Registre: {{ user.createdAt | date: 'dd/MM/yyyy' }}</p>
        </div>
        <div class="user-role" [class]="getRoleBadgeClass(user.rol)">
          {{ user.rol === 'ADMIN' ? 'üëë Admin' : 'üë§ Usuari' }}
        </div>
      </div>
    </div>

    <div *ngIf="!loading && filteredUsers.length === 0" class="no-results">
      No s'han trobat usuaris
    </div>
  </div>
</div>
