import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { BookService } from '@services/BookService';

@Component({
  selector: 'app-admin-books',
  templateUrl: './admin-books.component.html',
  styleUrl: './admin-books.component.css'
})
export class AdminBooksComponent implements OnInit {
  books: any[] = [];
  loading: boolean = true;
  showModal: boolean = false;
  editingBook: any = null;
  bookForm: any = {
    titol: '',
    autor: '',
    isbn: '',
    categoria: '',
    any: new Date().getFullYear(),
    quantitatTotal: 1,
    quantitatDisponible: 1,
    descripcio: ''
  };
  successMessage: string = '';
  errorMessage: string = '';
  isSaving: boolean = false;
  searchTerm: string = '';

  categories: string[] = [
    'Ficció', 'No Ficció', 'Ciència', 'Història', 'Tecnologia',
    'Art', 'Filosofia', 'Poesia', 'Teatre', 'Novel·la', 'Infantil'
  ];

  constructor(
    private bookService: BookService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadBooks();
  }

  loadBooks(): void {
    this.bookService.getAllBooks().subscribe(
      (books: any) => {
        this.books = books || [];
        this.loading = false;
      },
      (error: any) => {
        this.errorMessage = 'Error carregant els llibres';
        this.loading = false;
      }
    );
  }

  get filteredBooks(): any[] {
    if (!this.searchTerm) return this.books;
    const term = this.searchTerm.toLowerCase();
    return this.books.filter(book =>
      book.titol?.toLowerCase().includes(term) ||
      book.autor?.toLowerCase().includes(term) ||
      book.isbn?.toLowerCase().includes(term)
    );
  }

  openCreateModal(): void {
    this.editingBook = null;
    this.bookForm = {
      titol: '',
      autor: '',
      isbn: '',
      categoria: '',
      any: new Date().getFullYear(),
      quantitatTotal: 1,
      quantitatDisponible: 1,
      descripcio: ''
    };
    this.showModal = true;
  }

  openEditModal(book: any): void {
    this.editingBook = book;
    this.bookForm = { ...book };
    this.showModal = true;
  }

  closeModal(): void {
    this.showModal = false;
    this.editingBook = null;
    this.errorMessage = '';
  }

  saveBook(): void {
    if (!this.bookForm.titol || !this.bookForm.autor) {
      this.errorMessage = 'El títol i autor són obligatoris';
      return;
    }

    this.isSaving = true;
    this.errorMessage = '';

    if (this.editingBook) {
      this.bookService.updateBook(this.editingBook._id, this.bookForm).subscribe(
        (response: any) => {
          this.isSaving = false;
          this.successMessage = 'Llibre actualitzat correctament!';
          this.closeModal();
          this.loadBooks();
          setTimeout(() => this.successMessage = '', 3000);
        },
        (error: any) => {
          this.isSaving = false;
          this.errorMessage = error.error?.message || 'Error actualitzant el llibre';
        }
      );
    } else {
      this.bookService.createBook(this.bookForm).subscribe(
        (response: any) => {
          this.isSaving = false;
          this.successMessage = 'Llibre creat correctament!';
          this.closeModal();
          this.loadBooks();
          setTimeout(() => this.successMessage = '', 3000);
        },
        (error: any) => {
          this.isSaving = false;
          this.errorMessage = error.error?.message || 'Error creant el llibre';
        }
      );
    }
  }

  deleteBook(book: any): void {
    if (!confirm(`Estàs segur que vols eliminar "${book.titol}"?`)) return;

    this.bookService.deleteBook(book._id).subscribe(
      (response: any) => {
        this.successMessage = 'Llibre eliminat correctament!';
        this.loadBooks();
        setTimeout(() => this.successMessage = '', 3000);
      },
      (error: any) => {
        this.errorMessage = error.error?.message || 'Error eliminant el llibre';
      }
    );
  }

  goBack(): void {
    this.router.navigate(['/profile']);
  }
}
<div class="admin-container">
  <div class="admin-header">
    <div class="header-content">
      <button class="btn-back" (click)="goBack()">← Tornar</button>
      <h1>📚 Gestió de Llibres</h1>
      <button class="btn-add" (click)="openCreateModal()">+ Afegir Llibre</button>
    </div>
  </div>

  <!-- Messages -->
  <div *ngIf="successMessage" class="toast success">✓ {{ successMessage }}</div>
  <div *ngIf="errorMessage && !showModal" class="toast error">✕ {{ errorMessage }}</div>

  <div class="content">
    <!-- Search -->
    <div class="search-bar">
      <input type="text" [(ngModel)]="searchTerm" placeholder="🔍 Cercar per títol, autor o ISBN...">
      <span class="count">{{ filteredBooks.length }} llibres</span>
    </div>

    <!-- Loading -->
    <div *ngIf="loading" class="loading">
      <div class="spinner"></div>
      <p>Carregant llibres...</p>
    </div>

    <!-- Table -->
    <div *ngIf="!loading" class="table-container">
      <table>
        <thead>
          <tr>
            <th>Títol</th>
            <th>Autor</th>
            <th>ISBN</th>
            <th>Categoria</th>
            <th>Any</th>
            <th>Disponibles</th>
            <th>Accions</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let book of filteredBooks">
            <td class="title-cell">{{ book.titol }}</td>
            <td>{{ book.autor }}</td>
            <td class="isbn-cell">{{ book.isbn }}</td>
            <td><span class="category-badge">{{ book.categoria }}</span></td>
            <td>{{ book.any }}</td>
            <td>
              <span class="availability" [class.low]="book.quantitatDisponible < 2">
                {{ book.quantitatDisponible }}/{{ book.quantitatTotal }}
              </span>
            </td>
            <td class="actions-cell">
              <button class="btn-edit" (click)="openEditModal(book)">✏️</button>
              <button class="btn-delete" (click)="deleteBook(book)">🗑️</button>
            </td>
          </tr>
        </tbody>
      </table>
      <div *ngIf="filteredBooks.length === 0" class="no-results">
        No s'han trobat llibres
      </div>
    </div>
  </div>

  <!-- Modal -->
  <div *ngIf="showModal" class="modal-overlay" (click)="closeModal()">
    <div class="modal" (click)="$event.stopPropagation()">
      <div class="modal-header">
        <h2>{{ editingBook ? '✏️ Editar Llibre' : '➕ Nou Llibre' }}</h2>
        <button class="btn-close" (click)="closeModal()">×</button>
      </div>
      <div class="modal-body">
        <div *ngIf="errorMessage" class="modal-error">{{ errorMessage }}</div>
        
        <div class="form-row">
          <div class="form-group">
            <label>Títol *</label>
            <input type="text" [(ngModel)]="bookForm.titol" placeholder="Títol del llibre">
          </div>
          <div class="form-group">
            <label>Autor *</label>
            <input type="text" [(ngModel)]="bookForm.autor" placeholder="Autor del llibre">
          </div>
        </div>

        <div class="form-row">
          <div class="form-group">
            <label>ISBN</label>
            <input type="text" [(ngModel)]="bookForm.isbn" placeholder="ISBN">
          </div>
          <div class="form-group">
            <label>Categoria</label>
            <select [(ngModel)]="bookForm.categoria">
              <option value="">Selecciona categoria</option>
              <option *ngFor="let cat of categories" [value]="cat">{{ cat }}</option>
            </select>
          </div>
        </div>

        <div class="form-row">
          <div class="form-group">
            <label>Any</label>
            <input type="number" [(ngModel)]="bookForm.any" min="1000" max="2030">
          </div>
          <div class="form-group">
            <label>Quantitat Total</label>
            <input type="number" [(ngModel)]="bookForm.quantitatTotal" min="1">
          </div>
          <div class="form-group">
            <label>Quantitat Disponible</label>
            <input type="number" [(ngModel)]="bookForm.quantitatDisponible" min="0">
          </div>
        </div>

        <div class="form-group full">
          <label>Descripció</label>
          <textarea [(ngModel)]="bookForm.descripcio" rows="3" placeholder="Descripció del llibre..."></textarea>
        </div>
      </div>
      <div class="modal-footer">
        <button class="btn-cancel" (click)="closeModal()" [disabled]="isSaving">Cancel·lar</button>
        <button class="btn-save" (click)="saveBook()" [disabled]="isSaving">
          {{ isSaving ? 'Guardant...' : (editingBook ? 'Actualitzar' : 'Crear') }}
        </button>
      </div>
    </div>
  </div>
</div>
