import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { LoanService } from '@services/LoanService';

@Component({
  selector: 'app-admin-loans',
  templateUrl: './admin-loans.component.html',
  styleUrl: './admin-loans.component.css'
})
export class AdminLoansComponent implements OnInit {
  loans: any[] = [];
  loading: boolean = true;
  searchTerm: string = '';
  filterStatus: string = 'all';
  successMessage: string = '';
  errorMessage: string = '';

  constructor(
    private loanService: LoanService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadLoans();
  }

  loadLoans(): void {
    this.loanService.getAllLoans().subscribe(
      (loans: any) => {
        this.loans = loans || [];
        this.loading = false;
      },
      (error: any) => {
        this.errorMessage = 'Error carregant els pr√©stecs';
        this.loading = false;
      }
    );
  }

  get filteredLoans(): any[] {
    let result = this.loans;
    
    // Filter by status
    if (this.filterStatus !== 'all') {
      result = result.filter(loan => loan.estat === this.filterStatus);
    }
    
    // Filter by search term
    if (this.searchTerm) {
      const term = this.searchTerm.toLowerCase();
      result = result.filter(loan =>
        loan.usuari?.nom?.toLowerCase().includes(term) ||
        loan.llibre?.titol?.toLowerCase().includes(term) ||
        loan.usuari?.email?.toLowerCase().includes(term)
      );
    }
    
    return result;
  }

  get activeCount(): number {
    return this.loans.filter(l => l.estat === 'ACTIU').length;
  }

  get returnedCount(): number {
    return this.loans.filter(l => l.estat === 'RETORNAT').length;
  }

  isOverdue(loan: any): boolean {
    const returnDate = new Date(loan.dataRetornaPrevista);
    const today = new Date();
    return returnDate < today && loan.estat === 'ACTIU';
  }

  getStatusClass(loan: any): string {
    if (this.isOverdue(loan)) return 'overdue';
    return loan.estat === 'ACTIU' ? 'active' : 'returned';
  }

  goBack(): void {
    this.router.navigate(['/profile']);
  }
}
<div class="admin-container">
  <div class="admin-header">
    <div class="header-content">
      <button class="btn-back" (click)="goBack()">‚Üê Tornar</button>
      <h1>üìã Gesti√≥ de Pr√©stecs</h1>
      <div class="stats">
        <span class="stat active">{{ activeCount }} actius</span>
        <span class="stat returned">{{ returnedCount }} retornats</span>
      </div>
    </div>
  </div>

  <!-- Messages -->
  <div *ngIf="successMessage" class="toast success">‚úì {{ successMessage }}</div>
  <div *ngIf="errorMessage" class="toast error">‚úï {{ errorMessage }}</div>

  <div class="content">
    <!-- Filters -->
    <div class="filters-bar">
      <input type="text" [(ngModel)]="searchTerm" placeholder="üîç Cercar per usuari o llibre...">
      <select [(ngModel)]="filterStatus">
        <option value="all">Tots els pr√©stecs</option>
        <option value="ACTIU">Nom√©s actius</option>
        <option value="RETORNAT">Nom√©s retornats</option>
      </select>
      <span class="count">{{ filteredLoans.length }} pr√©stecs</span>
    </div>

    <!-- Loading -->
    <div *ngIf="loading" class="loading">
      <div class="spinner"></div>
      <p>Carregant pr√©stecs...</p>
    </div>

    <!-- Loans Table -->
    <div *ngIf="!loading" class="table-container">
      <table>
        <thead>
          <tr>
            <th>Usuari</th>
            <th>Llibre</th>
            <th>Data Pr√©stec</th>
            <th>Data Retorn Prevista</th>
            <th>Data Retorn Real</th>
            <th>Estat</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let loan of filteredLoans" [class]="getStatusClass(loan)">
            <td class="user-cell">
              <div class="user-mini">
                <span class="avatar">{{ loan.usuari?.nom?.charAt(0)?.toUpperCase() || '?' }}</span>
                <div>
                  <strong>{{ loan.usuari?.nom || 'Usuari desconegut' }}</strong>
                  <small>{{ loan.usuari?.email }}</small>
                </div>
              </div>
            </td>
            <td class="book-cell">
              <strong>{{ loan.llibre?.titol || 'Llibre desconegut' }}</strong>
              <small>{{ loan.llibre?.autor }}</small>
            </td>
            <td>{{ loan.dataPrestec | date: 'dd/MM/yyyy' }}</td>
            <td>{{ loan.dataRetornaPrevista | date: 'dd/MM/yyyy' }}</td>
            <td>{{ loan.dataRetornaReal ? (loan.dataRetornaReal | date: 'dd/MM/yyyy') : '-' }}</td>
            <td>
              <span class="status-badge" [class]="getStatusClass(loan)">
                <span *ngIf="isOverdue(loan)">‚ö†Ô∏è Endarrerit</span>
                <span *ngIf="!isOverdue(loan) && loan.estat === 'ACTIU'">üìñ Actiu</span>
                <span *ngIf="loan.estat === 'RETORNAT'">‚úÖ Retornat</span>
              </span>
            </td>
          </tr>
        </tbody>
      </table>
      <div *ngIf="filteredLoans.length === 0" class="no-results">
        No s'han trobat pr√©stecs
      </div>
    </div>
  </div>
</div>
