import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { LoanService } from '@services/LoanService';

@Component({
  selector: 'app-my-loans',
  templateUrl: './my-loans.component.html',
  styleUrls: ['./my-loans.component.css']
})
export class MyLoansComponent implements OnInit {
  loans: any[] = [];
  loading: boolean = true;
  isReturning: boolean = false;
  errorMessage: string = '';
  successMessage: string = '';

  constructor(
    private loanService: LoanService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadLoans();
  }

  loadLoans(): void {
    this.loanService.getMyLoansUser().subscribe(
      (response: any) => {
        this.loans = response || [];
        this.loading = false;
      },
      (error: any) => {
        console.log('Error carregant pr√©stecs', error);
        this.errorMessage = error.error?.message || 'Error carregant els pr√©stecs';
        this.loading = false;
      }
    );
  }

  returnLoan(loanId: string): void {
    this.isReturning = true;
    this.errorMessage = '';
    this.loanService.returnLoan(loanId).subscribe(
      (response: any) => {
        this.isReturning = false;
        this.successMessage = 'Llibre retornat correctament!';
        setTimeout(() => {
          this.loadLoans();
          this.successMessage = '';
        }, 2000);
      },
      (error: any) => {
        this.isReturning = false;
        this.errorMessage = error.error?.message || 'Error retornant el llibre';
      }
    );
  }

  isOverdue(loan: any): boolean {
    const returnDate = new Date(loan.dataRetornaPrevista);
    const today = new Date();
    return returnDate < today && loan.estat !== 'RETORNAT';
  }

  goHome(): void {
    this.router.navigate(['/home']);
  }
}
<div class="my-loans-container">
  <div class="header">
    <h1>Els Meus Pr√©stecs</h1>
    <button class="btn-back" (click)="goHome()">Tornar al Home</button>
  </div>

  <div *ngIf="loading" class="loading">
    Carregant pr√©stecs...
  </div>

  <div *ngIf="!loading && loans.length === 0" class="empty-state">
    <p>No tens cap pr√©stec actiu</p>
  </div>

  <div *ngIf="!loading && loans.length > 0" class="loans-section">
    <div class="loans-grid">
      <div *ngFor="let loan of loans" class="loan-card" [ngClass]="{'returned': loan.estat === 'RETORNAT'}">
        <div class="loan-header">
          <div class="book-emoji">üìö</div>
          <h3>{{ loan.llibre?.titol }}</h3>
        </div>

        <div class="loan-details">
          <div class="detail-row">
            <span class="label">Autor:</span>
            <span class="value">{{ loan.llibre?.autor }}</span>
          </div>
          <div class="detail-row">
            <span class="label">Biblioteca:</span>
            <span class="value">{{ loan.biblioteca?.nom }}</span>
          </div>
          <div class="detail-row">
            <span class="label">Data de Pr√©stec:</span>
            <span class="value">{{ loan.dataPrestec | date: 'dd/MM/yyyy' }}</span>
          </div>
          <div class="detail-row">
            <span class="label">Data de Devoluci√≥:</span>
            <span class="value" [ngClass]="isOverdue(loan) ? 'overdue' : ''">
              {{ loan.dataRetornaPrevista | date: 'dd/MM/yyyy' }}
            </span>
          </div>
          <div class="detail-row">
            <span class="label">Estat:</span>
            <span class="status" [ngClass]="loan.estat">
              {{ loan.estat }}
            </span>
          </div>
        </div>

        <button 
          *ngIf="loan.estat === 'ACTIU'"
          class="btn-return" 
          (click)="returnLoan(loan._id)" 
          [disabled]="isReturning">
          {{ isReturning ? 'Retornant...' : 'Retornar Llibre' }}
        </button>
        <div *ngIf="loan.estat === 'RETORNAT'" class="returned-badge">
          ‚úÖ Retornat el {{ loan.dataRetornaReal | date: 'dd/MM/yyyy' }}
        </div>
      </div>
    </div>
  </div>

  <div *ngIf="successMessage" class="success">
    {{ successMessage }}
  </div>

  <div *ngIf="errorMessage" class="error">
    {{ errorMessage }}
  </div>
</div>
