import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '@services/AuthService';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  name: string = '';
  email: string = '';
  password: string = '';
  confirmPassword: string = '';
  errorMessage: string = '';
  successMessage: string = '';
  loading: boolean = false;

  constructor(
    private authService: AuthService,
    private router: Router
  ) {}

  register() {
    // Validar camps buits
    if (!this.name || !this.email || !this.password || !this.confirmPassword) {
      this.errorMessage = 'Tots els camps són obligatoris';
      return;
    }

    // Validar que les contrasenyes coincideixen
    if (this.password !== this.confirmPassword) {
      this.errorMessage = 'Les contrasenyes no coincideixen';
      return;
    }

    // Validar longitud de contrasenya
    if (this.password.length < 6) {
      this.errorMessage = 'La contrasenya ha de tenir almenys 6 caràcters';
      return;
    }

    this.loading = true;
    this.errorMessage = '';
    this.successMessage = '';

    const newUser = {
      username: this.name,
      email: this.email,
      password: this.password
    };

    this.authService.register(newUser).subscribe(
      (response: any) => {
        this.loading = false;
        this.successMessage = 'Registre exitós! Redirigint al login...';
        setTimeout(() => {
          this.router.navigate(['/login']);
        }, 2000);
      },
      (error: any) => {
        this.loading = false;
        this.errorMessage = error.error.message || 'Error en el registre';
      }
    );
  }

  loginLink() {
    this.router.navigate(['/login']);
  }
}
<div class="register-container">
  <div class="register-box">
    <h1>Registre - Biblioteca</h1>

    <div *ngIf="errorMessage" class="error">
      {{ errorMessage }}
    </div>

    <div *ngIf="successMessage" class="success">
      {{ successMessage }}
    </div>

    <form (ngSubmit)="register()">
      <div class="form-group">
        <label>Nom Complet:</label>
        <input
          type="text"
          [(ngModel)]="name"
          name="name"
          placeholder="Joan Perez"
          required
        />
      </div>

      <div class="form-group">
        <label>Email:</label>
        <input
          type="email"
          [(ngModel)]="email"
          name="email"
          placeholder="email@example.com"
          required
        />
      </div>

      <div class="form-group">
        <label>Contrasenya:</label>
        <input
          type="password"
          [(ngModel)]="password"
          name="password"
          placeholder="Contrasenya"
          required
        />
      </div>

      <div class="form-group">
        <label>Repetir Contrasenya:</label>
        <input
          type="password"
          [(ngModel)]="confirmPassword"
          name="confirmPassword"
          placeholder="Repetir contrasenya"
          required
        />
      </div>

      <button type="submit" [disabled]="loading">
        {{ loading ? 'Registrant...' : 'Registrar-se' }}
      </button>
    </form>

    <p class="login-link">
      Ja tens compte?
      <a (click)="loginLink()" style="cursor: pointer; color: #3498db;">
        Login aqui
      </a>
    </p>
  </div>
</div>
